import React, { useState } from 'react';
import HeroSection from './components/HeroSection';
import BusinessVerification from './components/BusinessVerification';
import AccountCreationForm from './components/AccountCreationForm';
import TermsConditions from './components/TermsConditions';
import SuccessConfirmation from './components/SuccessConfirmation';
import TestimonialCarousel from './components/TestimonialCarousel';
import axios from 'axios';

function App() {
  const [step, setStep] = useState(1);
  const [business, setBusiness] = useState(null);
  const [userData, setUserData] = useState(null);

  const handleSearch = (businessDetails) => {
    if (businessDetails.business_id) {
      setBusiness(businessDetails); // Store frontend-fetched business details
      setStep(2); // Advance to BusinessVerification
    } else {
      alert('Invalid business details. Please try again.');
    }
  };

  const handleConfirmBusiness = () => setStep(3);
  const handleTryAgain = () => setStep(1);

  const handleAccountSubmit = async (data) => {
    if (data.back) {
      setStep(2); // Go back to BusinessVerification
      return;
    }
    try {
      const response = await axios.post('http://localhost:5000/api/users', {
        fullName: data.fullName,
        email: data.email,
        mobileNumber: data.mobileNumber,
        smsOptIn: data.smsOptIn,
        password: data.password,
        website: data.website || null,
        isOwner: data.isOwner,
        business_id: business?.business_id,
        business_name: business?.name,
        business_address: business?.address,
        business_phone: business?.phone || null,
      });
      setUserData({
        userId: response.data.userId,
        ...data,
        business_id: business?.business_id,
      });
      setStep(4); // Advance to TermsConditions
    } catch (error) {
      console.error('Account creation failed:', error);
      alert('Error creating account: ' + (error.response?.data?.message || 'Please try again later.'));
    }
  };

  const handleAcceptTerms = (data) => {
    if (data?.back) {
      setStep(3); // Go back to AccountCreationForm
      return;
    }
    setStep(5); // Advance to SuccessConfirmation
  };

  const handleUpgrade = async () => {
    try {
      const res = await axios.post('http://localhost:5000/api/upgrade', {
        userId: userData?.userId,
        business_id: business?.business_id,
      });
      window.location.href = res.data.sessionUrl;
    } catch (error) {
      console.error('Upgrade failed:', error);
      alert('Upgrade failed: ' + (error.response?.data?.message || 'Please try again later.'));
    }
  };

  const handleContinueFree = () => {
    window.location.href = '/'; // Redirect to dashboard or homepage
  };

  return (
    <div>
      {step === 1 && <HeroSection onSearch={handleSearch} />}
      {step === 2 && (
        <BusinessVerification
          business={business}
          onConfirm={handleConfirmBusiness}
          onTryAgain={handleTryAgain}
        />
      )}
      {step === 3 && (
        <AccountCreationForm
          onSubmit={handleAccountSubmit}
          business={business}
        />
      )}
      {step === 4 && <TermsConditions onAccept={handleAcceptTerms} />}
      {step === 5 && (
        <SuccessConfirmation
          onUpgrade={handleUpgrade}
          onContinueFree={handleContinueFree}
        />
      )}
      <TestimonialCarousel />
    </div>
  );
}

export default App;
