import React, { useState, useEffect, useRef } from 'react';

function HeroSection({ onSearch }) {
  const [searchQuery, setSearchQuery] = useState('');
  const inputRef = useRef(null);

  useEffect(() => {
    if (!window.google) return;
    const autocomplete = new window.google.maps.places.Autocomplete(inputRef.current, {
      types: ['establishment'],
      fields: ['place_id', 'name', 'formatted_address', 'formatted_phone_number', 'photos'],
    });

    autocomplete.addListener('place_changed', () => {
      const place = autocomplete.getPlace();
      if (place.name) {
        setSearchQuery(place.name);
        onSearch(place.name); // or send place object directly
      }
    });
  }, []);

  return (
    <div className="relative h-screen w-full overflow-hidden">
      <img
        src="/background.jpg"
        alt="Happy customers"
        className="absolute inset-0 w-full h-full object-cover filter blur-sm"
      />

      {/* Logo */}
      <img src="/logo.png" alt="Swipe Savvy Logo" className="absolute top-6 left-6 h-12 z-10" />

      {/* Overlay Card */}
      <div className="relative z-10 flex flex-col items-center justify-center h-full">
        <div className="bg-white bg-opacity-90 p-8 rounded-lg max-w-2xl text-center shadow-lg">
          <h1 className="text-4xl font-bold mb-4">You've Been Selected for a Free Loyalty Listing</h1>
          <p className="text-xl mb-6">
            Your business has been recognized for its outstanding reputation. Join the Swipe Savvy Rewards Network ‚Äî completely free.
          </p>
          <div className="flex gap-2 w-full">
            <input
              ref={inputRef}
              type="text"
              placeholder="Enter your business name or phone number"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="flex-grow p-3 border rounded w-full"
            />
            <button
              onClick={() => onSearch(searchQuery)}
              className="bg-blue-600 text-white px-4 py-2 rounded whitespace-nowrap"
            >
              üîç Locate Your Business
            </button>
          </div>
          <p className="text-sm mt-2">We‚Äôll find your business and you can confirm the correct one in the next step.</p>
        </div>
      </div>

      {/* Testimonials section placeholder */}
      <div className="absolute bottom-0 w-full z-10 bg-white bg-opacity-80 py-2 text-center text-sm">
        <marquee>‚≠ê ‚ÄúSwipe Savvy helped us double our returning customers!‚Äù ‚Äî Boutique Caf√© | ‚≠ê ‚ÄúA must-have for small businesses!‚Äù ‚Äî Salon & More</marquee>
      </div>
    </div>
  );
}

export default HeroSection;
