import React, { useState } from 'react';
import HeroSection from './components/HeroSection';
import BusinessVerification from './components/BusinessVerification';
import AccountCreationForm from './components/AccountCreationForm';
import TermsConditions from './components/TermsConditions';
import SuccessConfirmation from './components/SuccessConfirmation';
import TestimonialCarousel from './components/TestimonialCarousel';
import axios from 'axios';

function App() {
  const [step, setStep] = useState(1);
  const [business, setBusiness] = useState(null);
  const [userData, setUserData] = useState(null);

  const handleSearch = async (searchData) => {
    try {
      const query = searchData.place_id ? searchData.place_id : searchData.query;
      const res = await axios.post('http://localhost:5000/api/business', { query });
      setBusiness({ ...res.data, ...searchData }); // Merge backend data with frontend details
      setStep(2);
    } catch (error) {
      console.error('Business lookup failed:', error);
    }
  };

  const handleConfirmBusiness = () => setStep(3);
  const handleTryAgain = () => setStep(1);
  const handleAccountSubmit = async (data) => {
    try {
      const res = await axios.post('http://localhost:5000/api/register', { ...data, businessId: business.id });
      setUserData(res.data);
      setStep(4);
    } catch (error) {
      console.error('Registration failed:', error);
    }
  };
  const handleAcceptTerms = () => setStep(5);
  const handleUpgrade = async () => {
    try {
      const res = await axios.post('http://localhost:5000/api/upgrade', { userId: userData.userId });
      window.location.href = res.data.sessionUrl;
    } catch (error) {
      console.error('Upgrade failed:', error);
    }
  };
  const handleContinueFree = () => {
    // Redirect to dashboard or homepage
    window.location.href = '/';
  };

  return (
    <div>
      {step === 1 && <HeroSection onSearch={handleSearch} />}
      {step === 2 && (
        <BusinessVerification
          business={business}
          onConfirm={handleConfirmBusiness}
          onTryAgain={handleTryAgain}
        />
      )}
      {step === 3 && <AccountCreationForm onSubmit={handleAccountSubmit} />}
      {step === 4 && <TermsConditions onAccept={handleAcceptTerms} />}
      {step === 5 && (
        <SuccessConfirmation onUpgrade={handleUpgrade} onContinueFree={handleContinueFree} />
      )}
      <TestimonialCarousel />
    </div>
  );
}

export default App;
