import React, { useState } from 'react';

function AccountCreationForm({ onSubmit }) {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phone: '',
    password: '',
    website: '',
    isAuthorized: false,
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <div className="max-w-2xl mx-auto p-4">
      <div className="flex justify-between mb-4">
        <div className="w-1/4 bg-blue-600 h-2 rounded"></div>
        <div className="w-1/4 bg-blue-600 h-2 rounded"></div>
        <div className="w-1/4 bg-gray-200 h-2 rounded"></div>
        <div className="w-1/4 bg-gray-200 h-2 rounded"></div>
      </div>
      <h2 className="text-2xl font-bold mb-4">Create Your Swipe Savvy Account</h2>
      <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <input
          type="text"
          placeholder="Full Name"
          value={formData.fullName}
          onChange={(e) => setFormData({ ...formData, fullName: e.target.value })}
          className="p-2 border rounded"
        />
        <input
          type="email"
          placeholder="Email"
          value={formData.email}
          onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          className="p-2 border rounded"
        />
        <input
          type="tel"
          placeholder="Mobile Number"
          value={formData.phone}
          onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
          className="p-2 border rounded"
        />
        <input
          type="password"
          placeholder="Password"
          value={formData.password}
          onChange={(e) => setFormData({ ...formData, password: e.target.value })}
          className="p-2 border rounded"
        />
        <input
          type="url"
          placeholder="Website or Social Link (Optional)"
          value={formData.website}
          onChange={(e) => setFormData({ ...formData, website: e.target.value })}
          className="p-2 border rounded"
        />
        <label className="flex items-center">
          <input
            type="checkbox"
            checked={formData.isAuthorized}
            onChange={(e) => setFormData({ ...formData, isAuthorized: e.target.checked })}
            className="mr-2"
          />
          I am the owner or authorized representative of this business.
        </label>
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded col-span-full">
          Continue
        </button>
      </form>
    </div>
  );
}

export default AccountCreationForm;
