import React, { useRef, useEffect } from 'react';

function HeroSection({ onSearch }) {
  const inputRef = useRef(null);

  useEffect(() => {
    if (!window.google || !inputRef.current) return;

    const autocomplete = new window.google.maps.places.Autocomplete(inputRef.current, {
      types: ['establishment'], // You can also use 'geocode' for address-based search
      fields: ['place_id', 'name', 'formatted_address', 'geometry'],
    });

    autocomplete.addListener('place_changed', () => {
      const place = autocomplete.getPlace();
      if (place && place.place_id && onSearch) {
        onSearch({
          placeId: place.place_id,
          name: place.name,
          address: place.formatted_address,
          location: place.geometry?.location?.toJSON(),
        });
      }
    });
  }, []);

  return (
    <div className="relative h-screen flex items-center justify-center">
      {/* Background image */}
      {/* <img
        src="/background.jpg"
        alt="Happy customers"
        className="absolute inset-0 w-full h-full object-cover filter blur-sm"
      /> */}

      <div className="bg-white bg-opacity-90 p-8 rounded-lg max-w-2xl text-center z-10">
        <img src="/logo.png" alt="Swipe Savvy Logo" className="h-12 mb-4 mx-auto" />
        <h1 className="text-4xl font-bold mb-2">You've Been Selected for a Free Loyalty Listing</h1>
        <p className="text-xl mb-6">
          Your business has been recognized for its outstanding reputation. Join the Swipe Savvy Rewards Network — completely free.
        </p>

        <div className="flex gap-2">
          <input
            type="text"
            ref={inputRef}
            placeholder="Enter your business name or address"
            className="flex-grow p-2 border rounded"
          />
          <button
            className="bg-blue-600 text-white px-4 py-2 rounded"
            disabled
          >
            <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
              <path d="M8 4a4 4 0 100 8 4 4 0 000-8zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z" />
            </svg>
          </button>
        </div>

        <p className="text-sm mt-2">We’ll find your business and you can confirm the correct one in the next step.</p>
      </div>
    </div>
  );
}

export default HeroSection;
