import React, { useState, useEffect, useRef } from 'react';

function HeroSection({ onSearch }) {
  const [searchQuery, setSearchQuery] = useState('');
  const inputRef = useRef(null);
  const autocompleteRef = useRef(null);

  // Load Google Maps JavaScript API dynamically
  useEffect(() => {
    const loadGoogleMapsScript = () => {
      if (window.google && window.google.maps && window.google.maps.places) {
        initializeAutocomplete();
        return;
      }

      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=${process.env.REACT_APP_GOOGLE_API_KEY}&libraries=places`;
      script.async = true;
      script.defer = true;
      script.onload = initializeAutocomplete;
      script.onerror = () => console.error('Failed to load Google Maps API');
      document.head.appendChild(script);
    };

    const initializeAutocomplete = () => {
      if (!inputRef.current) return;

      autocompleteRef.current = new window.google.maps.places.Autocomplete(inputRef.current, {
        types: ['establishment'], // Restrict to businesses
        fields: ['place_id', 'name', 'formatted_address', 'formatted_phone_number'], // Fields to retrieve
      });

      autocompleteRef.current.addListener('place_changed', () => {
        const place = autocompleteRef.current.getPlace();
        if (place && place.place_id) {
          setSearchQuery(place.name);
        }
      });
    };

    loadGoogleMapsScript();

    return () => {
      // Clean up Google Maps listeners
      if (autocompleteRef.current) {
        window.google.maps.event.clearInstanceListeners(autocompleteRef.current);
      }
    };
  }, []);

  const handleSearch = () => {
    // Use the input value as the query (place name or raw input)
    onSearch(searchQuery);
  };

  return (
    <div className="relative h-screen flex items-center justify-center">
      <img
        src="/background.jpg"
        alt="Happy customers"
        className="absolute inset-0 w-full h-full object-cover filter blur-sm"
      />
      <div className="bg-white bg-opacity-90 p-8 rounded-lg max-w-2xl text-center">
        <img src="/logo.png" alt="Swipe Savvy Logo" className="h-12 mb-4 mx-auto" />
        <h1 className="text-4xl font-bold mb-2">You've Been Selected for a Free Loyalty Listing</h1>
        <p className="text-xl mb-6">Your business has been recognized for its outstanding reputation. Join the Swipe Savvy Rewards Network — completely free.</p>
        <div className="flex gap-2">
          <input
            ref={inputRef}
            type="text"
            placeholder="Enter your business name or phone number"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="flex-grow p-2 border rounded"
          />
          <button
            onClick={handleSearch}
            className="bg-blue-600 text-white px-4 py-2 rounded"
          >
            <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
              <path d="M8 4a4 4 0 100 8 4 4 0 000-8zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z" />
            </svg>
          </button>
        </div>
        <p className="text-sm mt-2">We’ll find your business and you can confirm the correct one in the next step.</p>
      </div>
    </div>
  );
}

export default HeroSection;
