import React, { useState, useEffect, useRef } from 'react';
import { Loader } from '@googlemaps/js-api-loader';
import TestimonialCarousel from './TestimonialCarousel';

function HeroSection({ onSearch }) {
  const [searchQuery, setSearchQuery] = useState('');
  const [error, setError] = useState(null);
  const inputRef = useRef(null);
  const autocompleteRef = useRef(null);

  // Load Google Maps JavaScript API with Places library
  useEffect(() => {
    const loader = new Loader({
      apiKey: process.env.REACT_APP_GOOGLE_API_KEY,
      version: 'weekly',
      libraries: ['places'],
    });

    const initializeAutocomplete = async () => {
      if (!inputRef.current) return;

      try {
        // Load the Google Maps API
        await loader.load();

        // Initialize Places Autocomplete (Legacy)
        autocompleteRef.current = new google.maps.places.Autocomplete(inputRef.current, {
          types: ['establishment'], // Restrict to businesses
          fields: ['place_id', 'name', 'formatted_address', 'formatted_phone_number', 'photos'],
        });

        // Handle place selection
        autocompleteRef.current.addListener('place_changed', () => {
          const place = autocompleteRef.current.getPlace();
          if (place && place.place_id) {
            setSearchQuery(place.name);
            setError(null);
            const businessDetails = {
              id: place.place_id,
              name: place.name,
              address: place.formatted_address || '',
              phone: place.formatted_phone_number || '',
              imageUrl: place.photos && place.photos[0] ? place.photos[0].getUrl({ maxWidth: 400 }) : '',
            };
            onSearch(businessDetails);
          }
        });
      } catch (error) {
        console.error('Failed to initialize Places API:', error);
        setError('Failed to load autocomplete. Please try again.');
      }
    };

    initializeAutocomplete();

    return () => {
      if (autocompleteRef.current) {
        google.maps.event.clearInstanceListeners(autocompleteRef.current);
      }
    };
  }, [onSearch]);

  const handleSearch = () => {
    if (!autocompleteRef.current || !autocompleteRef.current.getPlace()) {
      setError('Please select a business from the autocomplete suggestions.');
      return;
    }
  };

  return (
    <div
    className="relative h-screen w-screen flex items-center justify-center bg-cover bg-center"
    style={{ backgroundImage: "url('/happy-customers.jpg')" }}
    >
      <div className="bg-white bg-opacity-95 p-8 rounded-lg shadow-lg max-w-2xl text-center">
        <img src="/logo.png" alt="Swipe Savvy Logo" className="h-12 mb-4 mx-auto" />
        <h1 className="text-4xl font-bold mb-2">You've Been Selected for a Free Loyalty Listing</h1>
        <p className="text-xl mb-6">Your business has been recognized for its outstanding reputation. Join the Swipe Savvy Rewards Network — completely free.</p>
        <div className="flex gap-2">
          <input
            ref={inputRef}
            type="text"
            placeholder="Enter your business name or phone number"
            value={searchQuery}
            onChange={(e) => {
              setSearchQuery(e.target.value);
              setError(null);
            }}
            className="flex-grow p-2 border rounded"
          />
          <button
            onClick={handleSearch}
            className="bg-blue-600 text-white px-4 py-2 rounded"
          >
            <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
              <path d="M8 4a4 4 0 100 8 4 4 0 000-8zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z" />
            </svg>
          </button>
        </div>
        <p className="text-sm mt-2">We’ll find your business and you can confirm the correct one in the next step.</p>
        {error && <p className="text-red-500 text-sm mt-2">{error}</p>}
      </div>
      <div className="absolute bottom-4 w-full max-w-4xl px-4 z-15">
        <div className="bg-opacity-90 p-4 rounded-lg shadow-lg">
          <TestimonialCarousel />
        </div>
      </div>
    </div>
  );
}

export default HeroSection;
