import React, { useState, useEffect, useRef } from 'react';

function HeroSection({ onSearch }) {
  const [input, setInput] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const autocompleteService = useRef(null);

  useEffect(() => {
    if (!autocompleteService.current && window.google) {
      autocompleteService.current = new window.google.maps.places.AutocompleteService();
    }
  }, []);

  const handleChange = (e) => {
    setInput(e.target.value);
    if (autocompleteService.current && e.target.value) {
      autocompleteService.current.getPlacePredictions(
        { input: e.target.value },
        (predictions) => {
          if (predictions) setSuggestions(predictions);
        }
      );
    } else {
      setSuggestions([]);
    }
  };

  const handleSelect = (place) => {
    setInput(place.description);
    setSuggestions([]);
    onSearch(place.description); // pass to App.js
  };

  return (
    <div className="relative h-screen w-full">
      <img
        src="/background.jpg"
        alt="Happy customers"
        className="absolute inset-0 w-full h-full object-cover filter blur-sm"
      />

      {/* Logo */}
      <div className="absolute top-4 left-4 z-20">
        <img src="/logo.png" alt="Swipe Savvy Logo" className="h-10" />
      </div>

      {/* Center card */}
      <div className="relative z-10 flex items-center justify-center h-full">
        <div className="bg-white bg-opacity-90 p-8 rounded-lg max-w-2xl shadow-xl text-center">
          <h1 className="text-3xl font-bold mb-2">You've Been Selected for a Free Loyalty Listing</h1>
          <p className="text-lg mb-4">
            Your business has been recognized for its outstanding reputation. Join the Swipe Savvy Rewards Network — completely free.
          </p>

          <div className="relative">
            <input
              type="text"
              value={input}
              onChange={handleChange}
              placeholder="Enter your business name or phone number"
              className="w-full p-3 border rounded mb-2"
            />
            {/* Suggestions Dropdown */}
            {suggestions.length > 0 && (
              <ul className="absolute bg-white shadow border w-full z-50 max-h-60 overflow-y-auto">
                {suggestions.map((s) => (
                  <li
                    key={s.place_id}
                    onClick={() => handleSelect(s)}
                    className="px-4 py-2 hover:bg-gray-100 cursor-pointer"
                  >
                    {s.description}
                  </li>
                ))}
              </ul>
            )}
          </div>

          <p className="text-sm mt-2 text-gray-600">
            We’ll find your business and you can confirm the correct one in the next step.
          </p>
        </div>
      </div>

      {/* Testimonials Section (optional, for later) */}
      <div className="absolute bottom-0 w-full bg-white bg-opacity-90 py-2 text-center text-sm">
        {/* Testimonial carousel can be inserted here */}
        ⭐⭐⭐⭐⭐ “Amazing reach for local stores!” – Happy Merchant
      </div>
    </div>
  );
}

export default HeroSection;
