import React, { useState } from 'react';
import PlacesAutocomplete, { geocodeByAddress, getLatLng } from 'react-places-autocomplete';
import axios from 'axios';

function HeroSection({ onSearch }) {
  const [address, setAddress] = useState('');
  const [error, setError] = useState(null);

  const handleSelect = async (selectedAddress) => {
    try {
      setError(null);
      const results = await geocodeByAddress(selectedAddress);
      if (!results || results.length === 0) {
        throw new Error('No results found for the selected address.');
      }
      const place = results[0];
      await getLatLng(place); // For validation, if needed

      // Fetch additional details from server
      const response = await axios.get(`http://localhost:5000/api/place-details/${place.place_id}`);
      const businessDetails = response.data;

      if (!businessDetails.business_id) {
        throw new Error('Missing business_id in place details response.');
      }

      console.log('Sending businessDetails:', businessDetails);
      onSearch(businessDetails);
      setAddress('');
    } catch (error) {
      console.error('Error fetching business details:', error);
      setError('Failed to fetch business details. Please try again.');
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Welcome to Swipe Savvy</h1>
      <p className="mb-4">Find your business to get started.</p>
      <PlacesAutocomplete value={address} onChange={setAddress} onSelect={handleSelect}>
        {({ getInputProps, suggestions, getSuggestionItemProps, loading }) => (
          <div>
            <input
              {...getInputProps({
                placeholder: 'Search for your business',
                className: 'p-2 w-full border rounded',
              })}
            />
            {error && <p className="text-red-500 text-sm mt-2">{error}</p>}
            <div className="mt-2">
              {loading && <div>Loading...</div>}
              {suggestions.map((suggestion) => (
                <div
                  {...getSuggestionItemProps(suggestion)}
                  key={suggestion.placeId}
                  className="p-2 hover:bg-gray-100 cursor-pointer"
                >
                  {suggestion.description}
                </div>
              ))}
            </div>
          </div>
        )}
      </PlacesAutocomplete>
    </div>
  );
}

export default HeroSection;
