import React, { useState, useEffect, useRef } from 'react';

function HeroSection({ onSearch }) {
  const [searchQuery, setSearchQuery] = useState('');
  const inputRef = useRef(null);

  useEffect(() => {
    if (!window.google) return;

    const autocomplete = new window.google.maps.places.Autocomplete(inputRef.current, {
      types: ['establishment'], // restrict to businesses
      fields: ['place_id', 'name', 'formatted_address', 'formatted_phone_number', 'photos'],
    });

    autocomplete.addListener('place_changed', () => {
      const place = autocomplete.getPlace();
      if (place.name) {
        setSearchQuery(place.name);
        onSearch(place.name); // send the place name to parent or handler
      }
    });
  }, [onSearch]);

  return (
    <div className="relative h-screen w-full overflow-hidden">
      {/* Background Image with blur */}
      <img
        src="/background.jpg"
        alt="Happy customers"
        className="absolute inset-0 w-full h-full object-cover filter blur-sm"
      />

      {/* Logo top-left */}
      <img
        src="/logo.png"
        alt="Swipe Savvy Logo"
        className="absolute top-6 left-6 h-12 z-10"
      />

      {/* Centered card overlay */}
      <div className="relative z-10 flex flex-col items-center justify-center h-full px-4">
        <div className="bg-white bg-opacity-90 p-8 rounded-lg max-w-2xl w-full text-center shadow-lg">
          <h1 className="text-4xl font-bold mb-4">
            You've Been Selected for a Free Loyalty Listing
          </h1>
          <p className="text-xl mb-6">
            Your business has been recognized for its outstanding reputation. Join the Swipe Savvy Rewards Network ‚Äî completely free.
          </p>

          {/* Search Input and Button */}
          <div className="flex gap-2 w-full max-w-md mx-auto">
            <input
              ref={inputRef}
              type="text"
              placeholder="Enter your business name or phone number"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="flex-grow p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <button
              onClick={() => onSearch(searchQuery)}
              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-3 rounded-md flex items-center justify-center whitespace-nowrap"
            >
              <span className="mr-2 text-lg">üîç</span> Locate Your Business
            </button>
          </div>

          <p className="text-sm mt-2 text-gray-700">
            We‚Äôll find your business and you can confirm the correct one in the next step.
          </p>
        </div>
      </div>

      {/* Testimonials marquee at bottom */}
      <div className="absolute bottom-0 w-full z-10 bg-white bg-opacity-80 py-2 text-center text-sm">
        <marquee behavior="scroll" direction="left" scrollamount="5" className="select-none">
          ‚≠ê ‚ÄúSwipe Savvy helped us double our returning customers!‚Äù ‚Äî Boutique Caf√© &nbsp;&nbsp;&nbsp;|&nbsp;&nbsp;&nbsp;
          ‚≠ê ‚ÄúA must-have for small businesses!‚Äù ‚Äî Salon & More &nbsp;&nbsp;&nbsp;|&nbsp;&nbsp;&nbsp;
          ‚≠ê ‚ÄúExceptional support and growth!‚Äù ‚Äî Local Bookstore
        </marquee>
      </div>
    </div>
  );
}

export default HeroSection;
